INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_5', 'Pleasure to see you again, friend. How can I help you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_6', 'You are always welcome here, friend. What is on your mind?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_7', 'Hello, my friend. I hope you have come to benefit both our great nations?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_8', 'It''s great to see a friendly face. How may I help you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_9', 'Welcome, friend. Please stay. I shall have my servants prepare a wondrous feast to celebrate your arrival.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_10', 'Today we meet, my friend. How can my glorious nation help you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_11', 'I had a feeling your friendly face would show up around here. What can my empire do to assist an honorable leader such as yourself?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_12', 'My citizens have been pleased with contact from your empire. This can only benefit us both. Now, how may I help you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_13', 'Great to see a friend again. How may we help you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_14', 'You''ve come to the right place for diplomacy. What do you wish to discuss?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_7', 'Hello, what business do you have with me today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_8', 'How can I help you? And how can you help me?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_9', 'Hello there. I hope you come before me with honest intentions.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_10', 'Hello there. May I present you with some of my finest wine while we converse?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_11', 'How may I help you today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_12', 'Greetings. What affairs do you wish to discuss today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_NEUTRAL_HELLO_13', 'Hello. How may I assist you today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_6', 'I have pressing matters to attend to. Do not waste my time here.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_7', 'Your company is not welcome here. I pray you''ve made your visit to my land short.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_8', 'You are wasting your time here. What could you possibly have to offer me?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_9', 'Didn''t I send your emissary away? You have a lot of nerve coming to visit me.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_10', 'Don''t test me. You are not in a position to afford more hostility with us. Now what do you want?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_11', 'Speaking to you wasn''t on my list of priorities today. What do you want with my time?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_12', 'Oh, excellent. It''s you. Tell me, why have you disgraced me with your uncouth stench?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_13', 'You are undeserving of my time. Now, run along. So, why are you still here?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_14', 'It''s bad enough having to deal with your filth living in your own cove. Now you''ve dragged yourself to my presence?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HELLO_15', 'I would show my respect and offer you wine, but you are an undeserving little brute that only deserves to be fed poison. Now, get on with it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_4', 'We must stop this fighting. Both our people cry out to end this senseless violence.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_5', 'I am willing to negotiate a ceasefire, if you are. This war has gone on too long.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_6', 'Failure to end this war now only makes you look more the villain. Please, end this madness and let''s return to peace.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_7', 'Isn''t there a more civil way to end our hostilities?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_8', 'Fighting only makes this world worse. We are not barbarians. We must stop acting as such.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_9', 'Good leader, will you aid me in ending this tension? It is only my hope to build peace amongst our people.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_10', 'We ask for nothing more than a return to sensibilities.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_11', 'It''s time to put the past behind us and settle our differences. Bloodshed is not the answer.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_WANTS_PEACE_12', 'It''s time we realize that the only thing we''ve accomplished here is exchanged grief for our lost loved ones. We must end this war, for our people''s sake.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_5', 'Did you really think you were going to get out of this one so easily? Be gone!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_6', 'Why are you here? This is a joke, right?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_9', 'It''s time to face reality. I win, you lose.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_10', 'There shall be no peace. All your cities shall belong to us.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_11', 'It''s time to face reality. I win, you lose.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_12', 'Your land is filled with criminals, and I am their judge. Now get out of my sight, I have business to settle.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_13', 'Do you hear that? That''s the sound of your sinful crying out for salvation. Good thing I''m here.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_RESEARCH_AGREEMENT_2', 'I hope your scientists are working as hard as we are to fulfill our Research Agreement. I pray it becomes worth the money I spent.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_RESEARCH_AGREEMENT_3', 'Hail, mighty leader. Are you scientists hard at work completing our Research Agreement?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_RESEARCH_AGREEMENT_4', 'We spent a lot of gold on that Research Agreement. I hope a discovery is around the corner?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_2', 'Nice to see you again, friend. I hope your empire is in stable condition. How may I help you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_3', 'Hail, friend! How may I assist your great nation today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_4', 'It''s good to see you again, friend. Do you need something from me?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_WITH_5', 'Greetings, friend. We have business to conduct, yes?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_4', 'I am sick of {1_ThirdPlayerName:textkey}. I hope someday he will be expelled from our lives.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_5', 'I don''t understand how villains like {1_ThirdPlayerName:textkey} can call their people civilized. They must be stupid, too.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_WORKING_AGAINST_6', 'Wouldn''t you agree with me that {1_ThirdPlayerName:textkey} is a problem to the entire world? We must tell the others about their sin.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_2', 'You haven''t been destroyed yet? I''m surprised. I thought you barbarians were easier to defeat than that. One can only hope.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_3', 'You haven''t been crushed under the might of your enemies yet? Maybe I''ll have to step in. Wouldn''t that be nice?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_4', 'My advisers were just discussing your little war. I''m allowed to say we think it''s pretty pathetic.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_HUMAN_AT_WAR_5', 'My empire''s entertainers create many works about your little war. Let me be clear, your attempt at survival is kind of a joke.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HUMAN_AT_WAR_2', 'Sorry to hear you are in distracted by war. Can I help you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HUMAN_AT_WAR_3', 'Send my regards to your men in combat. Now, how may I service you today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_MILITARY_2', 'Don''t think I don''t notice your military encroaching on my borders. If you want a fight, then come. Don''t hide like a little coward.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_MILITARY_3', 'I am not threatened by your troops. Attack me like the "great" leader you are, or get out of my sight.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_MILITARY_2', 'My generals have noticed a large number of troops near my borders. I respectfully ask that you move them from my land, to keep my citizens happy. Now, did you have something to ask of me?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_MILITARY_3', 'I want to remain on good terms with you, but the positioning of your military is making that extremely difficult. Please reconsider the placement of your troops or we may have to withdraw from diplomacy with you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_EXPANSION_2', 'The expansion of your people is an annoyance to me and everyone who surrounds us. But I shouldn''t have to worry, your buildings will probably erode from that filth you call a nation.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_EXPANSION_3', 'You remind me of a swarm of locusts. You anger everyone and continue to spread until you''ve left your devastation of civility upon the entire world. I can only hope you realize how great of a headache your expansion has become.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_EXPANSION_2', 'It seems as if your expansion has been noticed by one of my foreign advisers. They suspect that you may run out of room and may open up arms against my people. I urge you to slow your expansion. What say you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_PLOT_BUYING_2', 'Throwing your money into the land around you only gives me more territory to capture. I hope you understand this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_PLOT_BUYING_2', 'I appreciate a fair purchase occasionally, but the speed at which you are buying up the land around us is alarming. I urge you to reconsider before tension arises between our nations.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_MILITARY_2', 'Your military is very powerful. I can only hope you will use your strength for good and not barbarism.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_MILITARY_3', 'I''ve heard legends of great heroes and battles from your empire. Your enemies should fear the day you two cross paths.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_ECONOMY_2', 'My merchants have been pleased with visits to your great empire. Perhaps you''d like to teach them a few things about making money?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_FRIENDLY_STRONG_ECONOMY_3', 'Your coffers are large, while mine remain empty. If you could teach my tax collectors how to maintain a large economy, I would be very appreciative.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_BY_AI_3', 'I spoke out against you for a reason. Why do you bother me with your nonsense? Leave.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DENOUNCED_AI_3', 'Not so angry now, are we? I''m not buying it. You''re fake. Out of my sight.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TOO_SOON_FOR_DOF_3', 'An intriguing offer. While I appreciate the politeness, I haven''t known you long enough to trust you yet. Perhaps some other time?.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TOO_SOON_FOR_DOF_4', 'You seem like a friendly leader, but I cannot attach myself to Alliances yet. Perhaps in the future?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DOF_NOT_HONORED_3', 'You bathed me in lies and then stabbed me in the back! You are worth no one''s attention. Out of my sight![NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DOF_NOT_HONORED_4', 'You remind me of a greedy child. You want too much of too many people and you end up with nothing. You are a liar and now everyone will know of your backstabbing ways.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DENOUNCING_FRIEND_3', 'You have shown your true colors. The only action I can take now is to warn others of your worthlessness.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DENOUNCING_FRIEND_4', 'You are not worth anyone''s time. Working with you was the biggest mistake of my life. I am destined not to repeat it.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOF_SO_AI_DENOUNCE_2', 'You and your little friend {1_CivName} make me sick. I can''t wait to see the day your empires burn in flames.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HOSTILE_AGGRESSIVE_MILITARY_WARNING_2', 'I will not stand around and play a fool, worm. If you want to fight, then fight.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HOSTILE_AGGRESSIVE_MILITARY_WARNING_3', 'I dare you to attack. Your cities will look grand decorated with my flags.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HOSTILE_AGGRESSIVE_MILITARY_WARNING_4', 'Do you wish to spill blood? Then come. I can''t wait.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_EXPANSION_SERIOUS_WARNING_2', 'Didn''t I warn you about your reckless expansion? This is a serious issue. Get away from my land, or else military action may follow. Understand?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DENOUNCE_4', 'You are a devil that deserves no recognition.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DENOUNCE_5', 'You are a foolish waste of time. Rot in your corner of the world, as I have no time for you.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DENOUNCE_6', 'Now the world knows that an idiot leads your pathetic nation. You will drive your people to an early extinction.[NEWLINE][NEWLINE]([COLOR_WARNING_TEXT]They have publicly denounced us! NOTE: You are not at war.[ENDCOLOR])', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_LAND_4', 'Our close borders have sparked too much tension. It is now time to see who stands victorious in combat.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_LAND_5', 'It''s time to expand my empire. I declare war on you with no regrets.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_5', 'I still can''t believe you thought I was your ally. Your ignorance will be your downfall.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_6', 'Being friends was nice while it lasted, wasn''t it? Sorry to break it to you, but you were merely a pawn in my grand strategy. Time to die.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_BETRAYAL_7', 'Time to die, whelp. Playing the role as your friend was getting boring. It''s time to assert my dominance.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_WEAK_BETRAYAL_2', 'I know I have a small chance at victory, but if I do not attack now, I will only face a stronger opponent in the future.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_PLOT_BUYING_WARNING_GOOD_2', 'I''m happy to hear you understand our predicament. I hope we can continue our mutual understanding.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_EXPANSION_WARNING_GOOD_2', 'Thank you for your understanding. We shall not forget this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_BAD_5', 'I was so confident you would understand. You have disappointed my people.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_BAD_6', 'Then out of my sight. You are a disgrace to my people.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_GOOD_2', 'Very well. I''m keeping an eye out for your armies, however.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_GOOD_3', 'Then see to it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_BAD_2', 'Pathetic whelp. Well, I guess I can''t fault you. It''s in your barbaric nature to make impudent decisions.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_BAD_3', 'Honest or not, your foolish choices has left me with no respect for you. Now, our armies shall clash.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HOSTILE_AGGRESSIVE_MILITARY_WARNING_BAD_4', 'You may not be a coward, but that doesn''t mean you''re not a fool. You are a disgrace to all leaders.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_AGGRESSIVE_MILITARY_WARNING_BAD_2', 'You have betrayed our trust. I am disappointed.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_AGGRESSIVE_MILITARY_WARNING_BAD_3', 'If this is the way it must be, I shall destroy you and your wicked armies.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_4', 'You have shown compassion where others have shown greed. For this, I applaud you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_5', 'By liberating our citizens, you have shown yourself to be a more trustworthy ally that I thought. I hope these good relations can continue.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_6', 'I am blessed to receive my people again. You have my thanks.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_7', 'You have returned these prisoners of war to me! This gesture shall not be forgotten.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RETURNED_CIVILIAN_8', 'Thank you for freeing my people. You are a kind-hearted and compassionate leader.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_4', 'We should open a bottle of champagne to celebrate your kindness!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_5', 'I am grateful for your selfless display.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_HUMAN_RESPONSE_THANKFUL_6', 'Our people can only benefit from this. Thank you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_4', 'What deal will end our mutual suffering?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_5', 'I am ready to listen to peace talks. What is your offer?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_WHAT_WILL_HUMAN_OFFER_6', 'On behalf of my people, I urge you to reach a hasty agreement. What is the price for peace?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_DESPERATE_9', 'My advisers may tell me otherwise, but I''m the one in charge. I have little hope to win, but I must defeat you!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_4', ' It is now my time. I will rise up to become king of kings. Lesser nations like yourself shall bow before my might!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DOW_OPPORTUNITY_7', ' I cannot ignore this chance to strengthen my people.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_3', 'I''m curious to see your offer.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_4', 'Please, make your offer.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_5', 'Let''s see your proposal.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_5', 'While you settle uninhibited? No. I have a right to settle where I please.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_6', 'Why not? If you don''t like it - do something.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DONT_SETTLE_NO_7', 'Of course not, you rude bastard! I''ll keep this in mind. I don''t want to conduct negotiations with a brute.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RESEARCH_AGREEMENT_OFFER_2', 'The future is shining ahead of us and my scientists are ready to explore the unknown! Will you aid us with a mutual Research Agreement?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_RESEARCH_AGREEMENT_OFFER_3', 'One group of scientists is good. But imagine what two could accomplish? Will you assist us with a Research Agreement?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LUXURY_TRADE_2', 'Exchange of these Luxury Resources would make both of our people happy. What say you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_REQUEST_ITEM_4', 'Bad times have fallen over my people. I request your aid to assist us. Can you spare this for a friend?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_REQUEST_ITEM_5', 'Friends need to help each other out. I have fallen on hard times. Can you assist us? I shall not forget it.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_OFFER_4', 'I think the time has come to lay down our arms and negotiate peace. Will you sign a peace treaty with us?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_OFFER_5', 'We are not bloodthirsty barbarians. We must end this war while we still have our dignity. Will you accept this deal?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_PEACE_OFFER_6', 'Today a child came up to me and told me his father was slaughtered by your men. His life was ruined. These people are our future. We must not betray them. Please, accept this peace offering.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WORK_WITH_US_YES_4', 'Excellent. I look forward to an Alliance that will change the world.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WORK_WITH_US_YES_5', 'I graciously accept, under the terms that we benefit both our nations and we fight the evil that lie outside our borders.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_INSULT_GENERIC_3', 'I don''t know how your empire manages to remain afloat. You''d think your people would join an empire worth living in.  Come to think of it, my nation would be a great destination!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WARMONGER_5', 'People are starting to talk about you. They seem to think you favor genocide over civil human discussion.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_WARMONGER_6', 'You spread fear where you walk. The other nations are starting to get worried. I can''t say the same for myself, I just think you''re a babbling idiot.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_BOOT_KISSING_3', 'Oh, great leader. I pray you can teach me the ways of leading a great empire such as yours.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_3', 'Please, I beg you. Show mercy for my people, they do not wish to die.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_4', 'You have broken my people to nothing. Their will is shattered, their homes ruined, families scattered. I can only hope you''ve come to end this madness before more innocents die.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLOMACY_FRIENDSHIP_ADV_QUEST', 'Alliance', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLOMACY_FRIENDSHIP_HEADING3_BODY', 'An Alliance between two or more players means, in the most basic sense that these players now have a mutual interest in looking out for each other (typically this involves becoming trade partners and sharing resources). Players may also choose to establish blocks of Allies with multiple players in order to ensure long-term safety and economic stability.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLOMACY_FRIENDSHIP_HEADING3_TITLE', 'Alliance', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_NTFN_DOF_S', '{1_PlayerName} allied with {2_PlayerName}!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_NTFN_DOF', '{1_CivName:textkey} and {2_CivName:textkey} formed an Alliance, forging a strong bond between the two empires.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_NOTIFICATION_DOF_S', '{1_PlayerName} allied with {2_PlayerName}!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_NOTIFICATION_DOF', '{1_CivName:textkey} and {2_CivName:textkey} formed an Alliance, forging a strong bond between the two empires.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_NOTIFICATION_FRIENDSHIP_EXPIRED_S', 'Alliance Expired', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_NOTIFICATION_FRIENDSHIP_EXPIRED', 'Your Alliance with {1_LeaderName} ran its course and will need to be renewed.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_DOF', '[COLOR_POSITIVE_TEXT]We are allies![ENDCOLOR]', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_MUTUAL_DOF', '[COLOR_POSITIVE_TEXT]We are allies with the same leaders![ENDCOLOR]', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_HUMAN_DOF_WITH_ENEMY', '[COLOR_NEGATIVE_TEXT]You allied with one of their enemies![ENDCOLOR]', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_REFUSED_REQUESTS', '[COLOR_NEGATIVE_TEXT]You refused a request made by this player after forming an Alliance![ENDCOLOR]', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_HUMAN_FRIEND_DECLARED_WAR', '[COLOR_NEGATIVE_TEXT]We declared war on them while allied![ENDCOLOR]', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_HUMAN_FRIEND_DENOUNCED', '[COLOR_NEGATIVE_TEXT]We denounced them while allied![ENDCOLOR]', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_HUMAN_DECLARED_WAR_ON_FRIENDS', '[COLOR_NEGATIVE_TEXT]You declared war on your allies![ENDCOLOR]', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_HUMAN_DENOUNCED_FRIENDS', '[COLOR_NEGATIVE_TEXT]You denounced your allies![ENDCOLOR]', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_DISCUSS_MESSAGE_DEC_FRIENDSHIP', 'Shall we form an Alliance?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_1', 'It is hard to find worthy friends in this world. I''d like to form an Alliance, informing the other empires of the bond between our peoples. What do you say?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_2', 'Great leader, would you care to ally with us? My hope is that we can build a long-lasting bond between our two empires.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_3', 'Let us plant the seeds of a bright future by forming an Alliance. What say you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_4', 'This world can be cruel. Only with the assistance of others can we overcome it. Shall we announce to the world we are allies?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_5', 'Hello, great leader. Will you ally with us to strengthen the bond of our empires?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_6', 'Only by forming an Alliance can our empires truly be a feared force in our world. Will you join me in this honor?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_7', 'I''m looking for a friend in this world. Will you ally with us?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DEC_FRIENDSHIP_8', 'Great leader, will you ally with us? It would make our friends sing for joy and enemies cower before our might.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_3', 'It seems I have launched an attack on one of your city states. It should be no loss to you; they were probably planning on backstabbing you anyway.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_4', 'Right now I am celebrating with a great feast. Launching an attack against your little city state was the best move I''ve made in years.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HOSTILE_WE_ATTACKED_YOUR_MINOR_5', 'Oh, do you hear something? I think it may be the cries of your little protectorate. No worries, my men shall silence them soon.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_3', 'My deepest regrets, but it seems that a protectorate of yours which you have had relations with in the past has been attacked by my armies. Rest assured, this decision was not made against you, but it was deemed a necessary course of action.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_4', 'Forgive me, but my armies may have overstepped their boundaries and attacked a city-state that you''ve dealt with in the past. Will you please offer your forgiveness?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_WE_ATTACKED_YOUR_MINOR_5', 'While my intention was not to sour relations with you, my armies have attacked a city-state in which have dealt with you in the past. I would like to repeat that this was not a hostile course of action towards you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_DISCUSS_SETTLE_WHAT_WE_PLEASE', 'We''ll expand wherever we please.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_DISCUSS_SORRY_FOR_SETTLING', 'Our apologies, we''ll refrain from settling near you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_EXPANSION_WARNING_5', 'I''ve noticed your borders have been expanding at an alarming rate. Settling cities near my territory could ruin any possibility of friendship for our nations.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_EXPANSION_WARNING_6', 'Ancient histories of my people tell of a migration from the lands you currently occupy. We will let you keep the lands for now, but we shall not hesitate to take action if you expand further into the lands of our ancestors.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_VICTORY_DISPUTE', '[COLOR_NEGATIVE_TEXT]They believe your interests conflict with theirs, and they don''t like it!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_MINOR_THIS_GUY_WANTS_WAR', 'The leader you''re speaking with has no desire to go to peace with this nation.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DIPLO_MINOR_OTHER_GUY_WANTS_WAR', 'The other leader has no desire to go to peace with the leader you''re speaking with.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PROGRESS_SCREEN_GOLD', 'The Richest Nations of the World', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PROGRESS_SCREEN_HAPPINESS', 'The Happiest Nations of the World', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PROGRESS_SCREEN_POWER', 'The Strongest Militaries of the World', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PROGRESS_SCREEN_PRODUCTION', 'The Most Productive Nations of the World', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PROGRESS_SCREEN_SCIENCE', 'The Most Advanced Nations of the World', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_PROGRESS_SCREEN_WONDERS', 'The Most Artistic Nations of the World', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DUDE_8', 'Plato', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DUDE_9', 'Aristotle', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DUDE_10', 'Magellan', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DUDE_11', 'Charles Dezobry', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DUDE_12', 'Louis Halphen', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_DUDE_13', 'Barclay', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_5', 'Is total extermination what you really want? I beg you to spare us.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_6', 'This war has destroyed us. I cannot offer you anything more than peace now. Please, show mercy.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_7', 'My people have no chance of survival. Our fate is in your hands, now. Please, spare us.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_DESTRUCTION_LOOMS_8', 'You have reduced my empire to nothing more than a glorified pile of rubble. I beg you to allow us to rebuild. For the sake of our relations.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ALEXANDER_FIRSTGREETING_4', 'Greetings, great leader! You are the mighty leader that Hermes said would enter my presence?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ALEXANDER_FIRSTGREETING_5', 'May the Gods be with us both, great leader. I am Alexander III, king of almighty Greece! I am honored to meet you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ALEXANDER_FIRSTGREETING_6', 'Greetings, strange one! I am Alexander, ruler of the mighty Greeks! I pray that you dine with the Olympians, and scorn the Titans.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ASKIA_FIRSTGREETING_5', 'By the glory of God, I greet you. I am Askia, ruler of the mighty Songhai. May our two nations live together in peace.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ASKIA_FIRSTGREETING_6', 'Greetings, great leader. I hope you are as learned a fellow as I am. May the grace of God keep our two nations from conflict.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ASKIA_FIRSTGREETING_7', 'Greetings! I am Askia of the fearless Songhai. I pray that you are a good friend and not a pitiful enemy.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_AUGUSTUS_FIRSTGREETING_4', 'Please, come closer. My name is Augustus Caesar, Emperor of the glorious empire of Rome. I pray you are a trustworthy leader.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_AUGUSTUS_FIRSTGREETING_5', 'The Roman people greet you. I am Augustus, Emperor of this mighty nation. Someday, my nation will grow to be the largest the world has ever seen!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_AUGUSTUS_FIRSTGREETING_6', 'Welcome to Rome, great leader. I shall serve our finest wine to celebrate the meeting of our two nations.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_BISMARCK_FIRSTGREETING_4', 'Welcome, great leader. The mighty nation of Germany greets you. My name is Bismarck. I pray your dealings with the German people are only positive.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_BISMARCK_FIRSTGREETING_5', 'Germany welcomes you to our land, stranger. I, Otto van Bismarck, hope that our nations will stay on good terms and benefit from this meeting..', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_BISMARCK_FIRSTGREETING_6', 'I greet thee, Stranger. Welcome to Germany. My name is Bismarck. Would you like a taste of this exquisite wine?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_CATHERINE_FIRSTGREETING_4', 'Hail, Stranger. I am Catherine, Empress of Russia. I hope we can get along well.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_CATHERINE_FIRSTGREETING_5', 'Hail, great leader. Welcome to glorious land of Russia. Our people are brave, and the men here are incredible! If your subjects are anything like mine, then we shall be the envy of the world!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_CATHERINE_FIRSTGREETING_6', 'Excellent to meet you, great leader. How can the beautiful Russian people service you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_DARIUS_FIRSTGREETING_4', 'Hail, leader. I am Darius of mighty Persia. Our people will be the envy of the world someday.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_DARIUS_FIRSTGREETING_5', 'Greetings, leader. My name is Darius, mighty king of Persia! I can only hope that you will prove to be a beneficial ally.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_DARIUS_FIRSTGREETING_6', 'Welcome to Persia, Stranger. I am Darius. Stay within your boundaries and our relationship should be fine.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ELIZABETH_FIRSTGREETING_5', 'England greets you, leader. I am Queen Elizabeth. How may we help you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ELIZABETH_FIRSTGREETING_6', 'Well met. I hope you prove useful to England.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ELIZABETH_FIRSTGREETING_7', 'Excellent to see another face in this world. I am Elizabeth, Queen of mighty England.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_GANDHI_FIRSTGREETING_4', 'Greetings, fellow equal! Let us sing in reverence to our newfound acquaintance!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_GANDHI_FIRSTGREETING_5', 'Hail, leader. Have you come to spread the word of peace to the world?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_GANDHI_FIRSTGREETING_6', 'Greetings. I am Gandhi, of the peaceful India. I pray our two nations will come together and further our nations.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_HARUN_FIRSTGREETING_4', 'By the grace of all that is glorious, I, Harun-al-Rashid, welcome you to the Arabian land.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_HARUN_FIRSTGREETING_5', 'Please, have a seat, great leader. I am Harun-al-Rashid, and history will forever remember this meeting.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_HARUN_FIRSTGREETING_6', 'Welcome to Arabia, great leader. My name is Harun-al-Rashid. How can I help you today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_HIAWATHA_FIRSTGREETING_4', 'The spirits have foretold your arrival, great leader.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_HIAWATHA_FIRSTGREETING_5', 'Greetings. I am Hiawatha, leader of the mighty Iroquois. I hope you share our love for nature.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_HIAWATHA_FIRSTGREETING_6', 'Hail, Stranger! The spirits have said many things about your people, and I am glad to have finally met you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_5', 'Ah! Who are you and why do you dare to present yourself to the mighty Montezuma?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_6', 'I am Montezuma, leader of the great Aztecs! And you? You are just one of my many subjects.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_7', 'Beware, Stranger! There are always sacrifices that must be made in Montezuma''s empire!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_MONTEZUMA_FIRSTGREETING_8', 'Do you wish to ally yourself with mighty Montezuma, Stranger? Send me your weak, your strong, your women and children! We shall spill blood in our gods'' name!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_NAPOLEON_FIRSTGREETING_5', 'France welcomes you, Stranger. I am Napoleon, feared general in all the world! Pray you remain France''s ally.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_NAPOLEON_FIRSTGREETING_6', 'Greetings, stranger. I am Napoleon, of France. The French shall not falter to anyone, keep this in mind.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_NAPOLEON_FIRSTGREETING_7', 'Greetings. I am Napoleon, and my people are fearless and brave. You can only hope yours are, too.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_NAPOLEON_TRIBUTE_NO_ANGRY', 'How dare you demand tribute from me!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_NAPOLEON_DECLAREWAR_4', 'Oh, I''m sure you''ve heard! My French armies rush out of the trees and from atop hills to destroy you once and for all!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ODA_FIRSTGREETING_4', 'Hail, leader. I hope you are as honorable and brave as I am.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ODA_FIRSTGREETING_5', 'Welcome to Japan. I am Oda Nobunaga. I pray that you will remain a loyal companion, or a worthy adversary.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_ODA_FIRSTGREETING_6', 'I am Oda Nobunaga, and I welcome you to the great empire of Japan. May your stay here be tranquil and engaging.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_RAMESSES_FIRSTGREETING_4', 'Greetings, mortal. I am Ramesses, pharaoh of glorious Egypt. Have you come to bow before my transcendence?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_RAMESSES_FIRSTGREETING_5', 'I am Ramesses, and you have entered the presence of my infinite divinity.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_RAMKHAMHAENG_FIRSTGREETING_4', 'Welcome to Siam, stranger! I am Ramkhamhaeng, king of these glorious people!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_RAMKHAMHAENG_FIRSTGREETING_5', 'Hail, great leader! Welcome to Siam. You are welcome to visit my garden whenever you please.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_RAMKHAMHAENG_FIRSTGREETING_6', 'Greetings! Welcome to Siam. Be warned, the wicked are not welcome in my land.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_SULEIMAN_FIRSTGREETING_4', 'I greet you, Stranger! I hope you prove to be a worthy leader!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_SULEIMAN_FIRSTGREETING_5', 'Hail, great leader! I greet you and welcome you to my court! I am Suleiman, leader of the Ottomans!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_SULEIMAN_FIRSTGREETING_6', 'Welcome to the Ottoman Empire, leader! I am pleased to meet you!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_WASHINGTON_FIRSTGREETING_4', 'America greets you, leader. I hope you are a ruler of freedom and prosperity.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_WASHINGTON_FIRSTGREETING_5', 'Welcome to America, Stranger. I am Washington, great ruler of the free world. How may we aid you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_WASHINGTON_FIRSTGREETING_6', 'Hello, leader. Welcome to America, land of the free and home of the brave. Now, tell me, what worth do you have for us?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_WUZETIAN_FIRSTGREETING_4', 'Greetings! I am Wu Zetian, of China. How may my people help you today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_WUZETIAN_FIRSTGREETING_5', 'This meeting will be celebrated by our descendants to come! Welcome to China, Stranger!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_LEADER_WUZETIAN_FIRSTGREETING_6', 'Greetings! How may I, Wu Zetian of mighty China, aid you and your foreign people?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LUXURY_TRADE_3', 'Our people demand a lavish lifestyle. Wouldn''t you agree?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LUXURY_TRADE_4', 'Agree to this deal and it will make the subjects of both our lands much happier.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LUXURY_TRADE_5', 'It''s time we traded our luxuries to provide a better life for our people. Wouldn''t you agree?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOF_SO_AI_DENOUNCE_3', 'First I had to deal with {1_CivName}, now I have to deal with you? Pathetic. You''re both the reason why this world is such a wretched hive of scum and villainy.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_HUMAN_DOF_SO_AI_DENOUNCE_4', 'How nice. It seems you and {1_CivName} are allies. It''s fine, because I will crush you both under the strength of my empire.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_EXPANSION_3', 'I''m sorry, but the rate of expansion amongst your people is troubling to me. I understand your desires to expand your influence, but our people are getting alarmed and feel threatened. I only ask if you would slow down and leave some room for us all, then we can all get along better. Do you agree?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_HOSTILE_AGGRESSIVE_PLOT_BUYING_3', 'Keep wasting your money on worthless land purchases. You probably can''t even keep them safe from wild animals, no less the armies of those who hate you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AGGRESSIVE_PLOT_BUYING_3', 'It seems you have been purchasing land around our territory far too quickly. My subjects do not appreciate this. I urge you to cease your actions, for the sake of both our people and to ease our tensions.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_14', 'Why have you come? To beg for peace? No! My armies will drive your cities to ground, women and children first!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_15', 'I shall never stop until your wicked are destroyed.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_AT_WAR_HOSTILE_16', 'Get away! This war shall continue until I am ready to give you mercy!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_15', 'Hail, friend! What do you require of my empire?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_16', 'Nice to see you again. Have you come to request a trade?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_GREETING_POLITE_HELLO_17', 'It''s great to see a friend, again. What matters shall we discuss today?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_6', 'I''m listening.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_LETS_HEAR_IT_7', 'Well? Let''s hear your proposal.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_3', 'I''m sorry, I don''t think this deal can work.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_4', 'There doesn''t seem to be a logical way to make this deal.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_5', 'I''m sorry, I can''t see a fair deal anywhere on this offer.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_NO_DEAL_POSSIBLE_6', 'No, I''m sorry. This deal just can''t work.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_4', 'That''s the best you can offer? What do you take me for, a fool?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_5', 'Come on, you''ll need to do better if you want to get a deal done.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_INSULTING_6', 'I could not shame my people with such an outrageous deal. You''ll have to do better than that.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_4', 'I cannot accept this deal. Please put more on the table.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_5', 'This deal isn''t good for my interests. Offer more and we can have a fair deal.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_REJECT_UNACCEPTABLE_6', 'I think you have to offer a little more for this deal to work. But as it stands now, this is not acceptable.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_HUMAN_CONCESSIONS_3', 'This deal will go a long way to making us friends again.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_HUMAN_CONCESSIONS_4', 'We will not forget this deal, I promise you this.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_4', 'This deal is done, then. May our trade continue into the future.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_5', 'Then the terms are set. This was a fair deal.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_ACCEPTABLE_6', 'I agree to this deal. It is fair for both parties involved.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_4', 'I cannot refuse this generous offer of yours. Thank you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_5', 'You have sacrificed your goods to make our relationship better. For this, I applaud you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_6', 'Your generosity has not gone unnoticed. Thank you.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_TRADE_ACCEPT_GENEROUS_7', 'We will remember this deal for years to come. Thank you for being so generous.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_3', 'Shall we open up our borders to make way for future trade and convenience?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_4', 'I think it''s in our best interests, as civilized nations of this world, to open our borders up and let our friendship thrive. What say you?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_OPEN_BORDERS_EXCHANGE_5', 'Let us open our borders to each other. It will only lead to increased friendship.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DEMAND_2', 'Let''s be real. You''ve got something I want, and you don''t hand it over, you can expect to see my troops knocking on your door.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-18'), 'TXT_KEY_GENERIC_DEMAND_3', 'Give me what I want, and your women and children shall remain safe. That sounds like a good deal, don''t you think?', '', '');
